import { makeAutoObservable } from "mobx";

export default class DeviceStore {
    constructor() {
        this._types = [
            {id: 1, name: 'Смартфоны'},
            {id: 2, name: 'Плантшеты'},
            {id: 3, name: 'Ноутбуки'},
            {id: 4, name: 'Моноблоки'}
        ]
        this._brands = [
            {id: 1, name: 'Apple'},
            {id: 2, name: 'Samsung'},
            {id: 3, name: 'Asus'},
            {id: 4, name: 'Lenovo'},
            {id: 5, name: 'Lenovo'},
            {id: 6, name: 'Lenovo'}
        ]
        this._devices = [
            {id: 1, name: 'iPhone 12 Pro', price: 100000, rating: 5, img: ''},
            {id: 2, name: 'Samsung A11', price: 70000, rating: 4, img: ''},
            {id: 3, name: 'ASUS ZenFone 9', price: 80000, rating: 3, img: ''},
            {id: 4, name: 'Lenovo K9 Play', price: 65000, rating: 4, img: ''}
        ]
        this._selectedType = {}
        this._selectedBrand = {}
        makeAutoObservable(this)
    }

    setTypes(types) {
        this._types = types
    }
    setBrands(brands) {
        this._brands = brands
    }
    setDevices(devices) {
        this._devices = devices
    }
    setSelectedType(type) {
        this._selectedType = type
    }
    setSelectedBrand(Brand) {
        this._selectedBrand = Brand
    }

    get types() {
        return this._types
    }
    get brands() {
        return this._brands
    }
    get devices() {
        return this._devices
    }
    get selectedType() {
        return this._selectedType
    }
    get selectedBrand() {
        return this._selectedBrand
    }
}
