            {id: 3, name: 'ASUS ZenFone 9', price: 80000, rating: 3, img: 'D:\RaDaR\React\online-shop\client\assets\tovar\asus-zenfone9.jpeg'},
            {id: 4, name: 'Lenovo K9 Play', price: 65000, rating: 4, img: 'D:\RaDaR\React\online-shop\client\assets\tovar\lenovo-K5-play.jpeg'}
        ]
        makeAutoObservable(this)
    }

